<?php class Albums_model extends CI_Model{	public function __construct()	{		$this->load->database();	}	public function get_all()	{		$query = "SELECT * FROM albums";		$result = $this->db->query($query);		return $result->result_array();	}	public function get_desc($album_id)	{		$query = "SELECT description FROM albums where id='$album_id'";		$result = $this->db->query($query);		return $result->result_array();	}}