<?php class Pastorsblog_model extends CI_Model{	public function __construct()	{		$this->load->database();	}	public function get_all()	{		$query = "SELECT id, header_title, title FROM pastorsblog";		$result = $this->db->query($query);		return $result->result_array();	}		public function get($id)	{		if (!$id) {			$query = "SELECT * FROM pastorsblog ORDER BY id DESC LIMIT 1";		}		else {			$query = "SELECT * FROM pastorsblog WHERE id=$id";		}		$result = $this->db->query($query);		return $result->result_array();	}}