<?php class prayerwall_model extends CI_Model{	public function __construct()	{		$this->load->database();        $this->load->helper('cookie');	}		public function get($limit=5, $page=1)	{        if ($limit == '') {            $limit = 5;        }        		$query = "SELECT id, wall_title, amen_count, like_count, 			message, user_amen_ids, user_like_ids, author,			picture, date_created, last_updated            FROM prayer_wall ORDER BY 			last_updated DESC LIMIT $limit";		$result = $this->db->query($query);						return $result->result_array(); 	}        public function get_by_date($prayerwall_last_updated, $limit=5)     {        if ($limit == '') {            $limit = 5;        }        		if ($prayerwall_last_updated != "REAPED_LAST") {                  if(!$prayerwall_last_updated) {				$start = $this->config->item('wall_limit');				/* first scroll down */ 				$query = "SELECT a.* FROM ( SELECT id, wall_title, amen_count, like_count, message, user_amen_ids, user_like_ids, author,	picture, date_created, last_updated FROM prayer_wall WHERE id > '$start' LIMIT $limit ) a ORDER BY a.id DESC";  			}						else {				$last_updated = $prayerwall_last_updated;                        				$query = "SELECT id, wall_title, amen_count, like_count, 					message, user_amen_ids, user_like_ids, author,					picture, date_created, last_updated FROM prayer_wall 					WHERE last_updated < '$last_updated' ORDER BY last_updated DESC LIMIT $limit" ;                // print $query;								}						$result = $this->db->query($query);        			$result_arr = $result->result_array(); 						// if($result_arr) {				// $this->input->set_cookie('prayerwall_last_updated', $result_arr[count($result_arr)-1]['last_updated'], 86400);            // }			// else {	                // $this->input->set_cookie('prayerwall_last_updated', 'REAPED_LAST', 86400);			// }            			return $result_arr;		}		else {			return "";		}    }		public function save($sns_type,		$author_sns_id, $author, $title, $message, $picture,        $link, $caption, $description) 	{		if (!$sns_type) {			return false;		}				$query = "INSERT INTO prayer_wall (sns_type, author_sns_id, 			author, wall_title, message, picture, link, caption, description, date_created)			VALUES('$sns_type', '$author_sns_id', '$author', '$title',			'$message', '$picture', '$link', '$caption', 			'$description', NOW())"; 				$this->db->query($query);        return mysql_insert_id();	}		public function amen($feed_id, $current_user_id, $picture, $name)	{				$query = "SELECT user_amen_ids from prayer_wall			WHERE id='$feed_id' LIMIT 1";					/*		$query = "UPDATE prayer_wall SET amen_count=(amen_count+1),			user_amen_ids=(CASE when(user_amen_ids) then 			CONCAT(user_amen_ids, '$tmp') else			CONCAT(':', '$tmp') end) 			WHERE id='$feed_id'";		*/				$result = $this->db->query($query);		$result = $result->result_array();		if ($result[0]['user_amen_ids']) {			$new_amen_ids = $result[0]['user_amen_ids'].strval($current_user_id).':';			if($current_user_id != "") {				$query = "UPDATE prayer_wall SET amen_count=(amen_count+1),					user_amen_ids='$new_amen_ids' WHERE id='$feed_id'";				}						}		else {			$new_amen_ids = ":".strval($current_user_id).":";			if($current_user_id != "") {				$query = "UPDATE prayer_wall SET amen_count=(amen_count+1),					user_amen_ids='$new_amen_ids' WHERE id='$feed_id'";			}		}		$this->db->query($query);				/* add to prayerwall amens */		$query = "INSERT INTO prayerwall_amens(			sns_id, prayerwall_id, picture, status, name) 			VALUES ('$current_user_id', '$feed_id', 			'$picture', 'AMENED', '$name')";        $this->db->query($query);        	}		public function get_amens($feed_id)	{		$query = "SELECT user_amen_ids FROM prayer_wall WHERE id='$feed_id'";		$result = $this->db->query($query);		return $result->result_array(); 	}		public function get_likes($feed_id)	{		$query = "SELECT user_like_ids FROM prayer_wall WHERE id='$feed_id'";		$result = $this->db->query($query);		return $result->result_array(); 	}		public function get_ameners($pw_id) 	{		$query = "SELECT name, picture from prayerwall_amens where prayerwall_id='$pw_id'";		$result = $this->db->query($query);		return $result->result_array();	}		public function is_amened($amens, $sns_id)	{		$tmp = ":$sns_id:";		if(strpos($amens, $tmp) !== false) {			return 1;		}		return 0;	}		public function is_liked($likes, $sns_id)	{		$tmp = ":$sns_id:";		if(strpos($likes, $tmp) !== false) {			return 1;		}		return 0;	}}