<?php class Track_model extends CI_Model{	public function __construct()	{		$this->load->database();	}	public function get_all_historical_glimpse()	{		$query = "SELECT id, header_title, title, day_in_int FROM historical_glimpse";		$result = $this->db->query($query);		return $result->result_array();	}		public function get_historical_glimpse($id)	{		if (!$id) {			$query = "SELECT * FROM historical_glimpse ORDER BY id DESC LIMIT 1";		}		else {			$query = "SELECT * FROM historical_glimpse WHERE id=$id";		}		$result = $this->db->query($query);		return $result->result_array();	}}