<?php class Weeklybulletin_model extends CI_Model{	public function __construct()	{		$this->load->database();	}	public function get_all()	{		$query = "SELECT id, header_title, title, day_in_int, series_title FROM weekly_bulletin";		$result = $this->db->query($query);		return $result->result_array();	}		public function get($id)	{		if (!$id) {			$query = "SELECT * FROM weekly_bulletin ORDER BY id DESC LIMIT 1";		}		else {			$query = "SELECT * FROM weekly_bulletin WHERE id=$id";		}		$result = $this->db->query($query);		return $result->result_array();	}}