<?php class Songs_model extends CI_Model{	public function __construct()	{		$this->load->database();	}	public function get_all()	{		$query = "SELECT * FROM songs";		$result = $this->db->query($query);		return $result->result_array();	}	public function get_featured()	{		$query = "SELECT * FROM songs WHERE featured=1";		$result = $this->db->query($query);		return $result->result_array();	}    public function get($id)    {		if($id < 1) {			$query = "SELECT * FROM songs ORDER BY id DESC LIMIT 1";		}		else { 			$query = "SELECT * FROM songs WHERE id=$id";		}				$result = $this->db->query($query);		$obj = $result->result_array();				if (!$obj) {			$query = "SELECT * FROM songs WHERE id=1";			$result = $this->db->query($query);				$obj = $result->result_array();		}		return $obj;    }}