<?php class Testimonies_model extends CI_Model{	public function __construct()	{		$this->load->database();	}	public function get_all_salvation()	{		$query = "SELECT * FROM salvation";		$result = $this->db->query($query);		return $result->result_array();	}    public function get_salvation($id)    {		if($id < 1) {			/* obtains last row */			$query = "SELECT * FROM salvation ORDER BY id DESC LIMIT 1";		}		else {			$query = "SELECT * FROM salvation WHERE id=$id";		}				$result = $this->db->query($query);		$obj = $result->result_array();				if (!$obj) {			/* obtains first row */			$query = "SELECT * FROM salvation WHERE id=1";			$result = $this->db->query($query);				$obj = $result->result_array();		}		return $obj;    }}